import React, { useState, useEffect, useRef } from 'react';
import { searchStocks, getStockData } from '../services/api';

function SearchBar({ onStockAdd }) {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    }

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Debounce search to avoid too many API calls
  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      if (searchTerm.length >= 2) {
        handleSearch();
      } else {
        setSearchResults([]);
        setShowDropdown(false);
      }
    }, 300);

    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm]);

  const handleSearch = async () => {
    if (!searchTerm) return;
    
    setIsLoading(true);
    try {
      const results = await searchStocks(searchTerm);
      setSearchResults(results);
      setShowDropdown(results.length > 0);
    } catch (error) {
      console.error('Error searching stocks:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSelectStock = async (stock) => {
    try {
      // Get full stock data
      const stockData = await getStockData(stock.symbol);
      
      // Add name from search results
      const completeStockData = {
        ...stockData,
        name: stock.name
      };
      
      onStockAdd(completeStockData);
      setSearchTerm('');
      setSearchResults([]);
      setShowDropdown(false);
    } catch (error) {
      console.error('Error getting stock data:', error);
    }
  };

  return (
    <div className="search-container" ref={dropdownRef}>
      <div className="search-input-wrapper">
        <input
          type="text"
          className="search-input"
          placeholder="Search for stocks..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onFocus={() => searchResults.length > 0 && setShowDropdown(true)}
        />
        {isLoading && <span className="search-loader"></span>}
      </div>
      
      {showDropdown && (
        <ul className="search-results">
          {searchResults.length === 0 ? (
            <li className="no-results">No results found</li>
          ) : (
            searchResults.map((result) => (
              <li 
                key={result.symbol} 
                className="search-result-item"
                onClick={() => handleSelectStock(result)}
              >
                <span className="result-symbol">{result.symbol}</span>
                <span className="result-name">{result.name}</span>
              </li>
            ))
          )}
        </ul>
      )}
    </div>
  );
}

export default SearchBar;
